import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Ensure you have Tailwind CSS set up or add the CDN to your HTML
// <script src="https://cdn.tailwindcss.com"></script>

export default function App() {
  // Session details retrieved from localStorage
  const [studyId, setStudyId] = useState("");
  const [sessionId, setSessionId] = useState("");
  const [smartphoneIp, setSmartphoneIp] = useState("");

  // Calibration parameters
  const [boardRows, setBoardRows] = useState(7);
  const [boardCols, setBoardCols] = useState(5);
  const [squareSize, setSquareSize] = useState(0.025); // in meters, e.g., 2.5 cm
  const [duration, setDuration] = useState(5); // Default video duration in seconds

  // UI state
  const [isCalibrating, setIsCalibrating] = useState(false);
  const [calibrationMessage, setCalibrationMessage] = useState("");

  // Use a useEffect hook to load the session details when the component mounts
  useEffect(() => {
    const storedStudyId = localStorage.getItem('studyId');
    const storedSessionId = localStorage.getItem('sessionId');
    const storedSmartphoneIp = localStorage.getItem('smartphoneIp');

    if (storedStudyId) setStudyId(storedStudyId);
    if (storedSessionId) setSessionId(storedSessionId);
    if (storedSmartphoneIp) setSmartphoneIp(storedSmartphoneIp);

    // Clear the message when the component loads
    setCalibrationMessage("");
  }, []);

  // Function to perform the entire calibration workflow
  const performFullCalibration = async () => {
    if (!studyId || !sessionId || !smartphoneIp) {
      setCalibrationMessage("Error: Session details are missing. Please go back to Session Initialization.");
      return;
    }

    setIsCalibrating(true);
    setCalibrationMessage("Starting smartphone video capture and calibration processing...");

    try {
      // API call to your Python backend
      const response = await axios.post("http://localhost:8000/capture-and-process-smartphone-calibration", {}, {
        params: {
          study_id: studyId,
          session_id: sessionId,
          smartphone_ip: smartphoneIp, // Added smartphoneIp to the request
          duration: duration,
          board_rows: boardRows,
          board_cols: boardCols,
          square_size: squareSize
        }
      });

      setCalibrationMessage(
        `Calibration successful! Reprojection Error: ${response.data.reprojection_error.toFixed(4)}`
      );
      console.log("Calibration results:", response.data);
    } catch (error) {
      console.error("Failed to calibrate camera:", error);
      setCalibrationMessage(
        `Failed to calibrate camera. Error: ${error.response?.data?.detail || error.message}`
      );
    } finally {
      setIsCalibrating(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white p-8 rounded-xl shadow-lg w-full max-w-2xl">
        <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">Calibration</h2>
        <p className="text-gray-600 mb-8 text-center">This page is dedicated to initiating and managing calibration procedures.</p>

        {/* Display the loaded session details */}
        <div className="bg-blue-50 border border-blue-200 p-4 rounded-lg mb-6">
          <h3 className="text-xl font-semibold text-blue-800 mb-2">Current Session Details</h3>
          <p className="text-blue-700"><strong>Study ID:</strong> {studyId || "Not set"}</p>
          <p className="text-blue-700"><strong>Session ID:</strong> {sessionId || "Not set"}</p>
          <p className="text-blue-700"><strong>Smartphone IP:</strong> {smartphoneIp || "Not set"}</p>
        </div>

        <h3 className="text-2xl font-semibold text-gray-800 mb-4 mt-8">Calibration Parameters</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div>
            <label htmlFor="board-rows" className="block text-sm font-medium text-gray-700">Inner Corners (Rows):</label>
            <input
              type="number"
              id="board-rows"
              value={boardRows}
              onChange={(e) => setBoardRows(Number(e.target.value))}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
              min="1"
            />
          </div>
          <div>
            <label htmlFor="board-cols" className="block text-sm font-medium text-gray-700">Inner Corners (Cols):</label>
            <input
              type="number"
              id="board-cols"
              value={boardCols}
              onChange={(e) => setBoardCols(Number(e.target.value))}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
              min="1"
            />
          </div>
          <div>
            <label htmlFor="square-size" className="block text-sm font-medium text-gray-700">Square Size (m):</label>
            <input
              type="number"
              id="square-size"
              value={squareSize}
              onChange={(e) => setSquareSize(Number(e.target.value))}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
              step="0.001"
              min="0.001"
            />
          </div>
          <div>
            <label htmlFor="duration" className="block text-sm font-medium text-gray-700">Video Duration (s):</label>
            <input
              type="number"
              id="duration"
              value={duration}
              onChange={(e) => setDuration(Number(e.target.value))}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
              min="1"
            />
          </div>
        </div>

        <h3 className="text-2xl font-semibold text-gray-800 mb-4 mt-8">Start Full Calibration Workflow</h3>
        <button
          onClick={performFullCalibration}
          disabled={isCalibrating || !studyId || !sessionId || !smartphoneIp}
          className={`w-full py-3 px-6 rounded-md text-white font-bold transition-colors duration-200
            ${isCalibrating ? 'bg-indigo-400 cursor-not-allowed' : 'bg-indigo-600 hover:bg-indigo-700'}
            disabled:opacity-50`}
        >
          {isCalibrating ? "Calibrating..." : "Start Full Calibration"}
        </button>

        {calibrationMessage && (
          <p className={`mt-4 p-3 rounded-lg text-sm text-center
            ${isCalibrating ? 'bg-yellow-100 text-yellow-800' : (calibrationMessage.includes("Error") ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800')}
          `}>
            {calibrationMessage}
          </p>
        )}

        <p className="mt-8 text-gray-500 text-sm italic text-center">
          *This will capture a video from the smartphone and process it for camera calibration in a single step.
        </p>
      </div>
    </div>
  );
}
