  return (
  <div style={{ padding: "2rem" }}>
    <h2>Video Recorder</h2>

    <div>
      <label>Session ID: </label>
      <input
        value={sessionId}
        onChange={(e) => setSessionId(e.target.value)}
      />
    </div>

    <div style={{ marginTop: "0.5rem" }}>
      <label>Filename: </label>
      <input
        value={filename}
        onChange={(e) => setFilename(e.target.value)}
      />
